import { Fragment, useState } from 'react'
import { Link, useRouteMatch } from 'react-router-dom'
import { Listbox, Transition } from '@headlessui/react'
import { CheckIcon, SelectorIcon } from '@heroicons/react/solid'

function classNames(...classes) {
  return classes.filter(Boolean).join(' ')
}

export default function GuideCategories({ categories }) {
  const [selected, setSelected] = useState(categories[0])

  return (
    // <div className="grid grid-cols-1 gap-4 sm:grid-cols-4">
    //   {categories.map((category) => (
    //     <div
    //       key={category.id}
    //       className="relative rounded-lg border border-gray-300 bg-white px-6 py-5 shadow-sm flex items-center space-x-3 hover:border-gray-400 focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500"
    //     >
    //       <div className="flex-1 min-w-0">
    //         <Link to={`/categories/${category.id}/guides`}>
    //           <span className="absolute inset-0" aria-hidden="true" />
    //           <p className="text-sm font-medium text-gray-900">{category.name}</p>
    //         </Link>
    //       </div>
    //     </div>
    //   ))}
    // </div>
      categories.length && selected ?
    <Link to={`/categories/${selected.id}/guides`}>
      <Listbox value={selected} onChange={setSelected}>
        {({ open }) => (
          <>
            <Listbox.Label className="block text-sm font-medium text-gray-700">Category</Listbox.Label>
            <div className="mt-1 relative">
              <Listbox.Button className="bg-white relative w-full border border-gray-300 rounded-md shadow-sm pl-3 pr-10 py-2 text-left cursor-default focus:outline-none focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm">
                <span className="block truncate">{selected.name}</span>
                <span className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
                  <SelectorIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                </span>
              </Listbox.Button>

              <Transition
                show={open}
                as={Fragment}
                leave="transition ease-in duration-100"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
              >
                <Listbox.Options className="absolute z-10 mt-1 w-full bg-white shadow-lg max-h-60 rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto focus:outline-none sm:text-sm">
                  {categories.map((category) => (
                    <Listbox.Option
                      key={category.id}
                      className={({ active }) =>
                        classNames(
                          active ? 'text-white bg-indigo-600' : 'text-gray-900',
                          'cursor-default select-none relative py-2 pl-3 pr-9'
                        )
                      }
                      value={category}
                    >
                      {({ selected, active }) => (
                        <>
                        {/* <Link to={`/categories/${category.id}/guides`}> */}
                          <span className={classNames(selected ? 'font-semibold' : 'font-normal', 'block truncate')}>
                            {category.name}
                          </span>

                          {selected ? (

                              <span
                              className={classNames(
                                active ? 'text-white' : 'text-indigo-600',
                                'absolute inset-y-0 right-0 flex items-center pr-4'
                              )}
                            >
                              <CheckIcon className="h-5 w-5" aria-hidden="true" />
                            </span>

                            ) : null}
                            {/* </Link> */}
                        </>
                      )}
                    </Listbox.Option>
                  ))}
                </Listbox.Options>
              </Transition>
            </div>
          </>
        )}
      </Listbox>
    </Link>
      : <div>No categories</div>
  )
}
