            <div className="mt-4">
              <h1 className="sr-only">Guides</h1>
              <GuideList
                items={guides}
                contentType="guides"
                setGuides={setGuides}
                user={user}
              />
            </div>
          </main>
        </div>
      </div>
    </div>

  )
}
