import { useEffect, useState } from 'react'

import SideBar from '../../components/sideBar'
import NavBar from '../../components/navBar'
import NewButton from '../../components/newButton'
import GuideList from './guideList'
import api from '../../util/api'
import GuideCategories from '../../components/guideCategories'

const tabs = [//need to change to category
    { name: 'Recent', href: '#', current: true },
    { name: 'Most Liked', href: '#', current: false },
    { name: 'Most Answers', href: '#', current: false },
]

function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
}

export default function GuidesPage() {
    const [user, setUser] = useState(null)
    const [guides, setGuides] = useState([])
    const [categories, setCategories] = useState([])
    const [error, setError] = useState(null)

    useEffect(() => {
        api.getUser()
            .then(response => setUser(response.data))
            .catch((error) => (
                setError(error)
            ))
    }, [])

    useEffect(() => {
        api.getCategories()
            .then((response) =>
                setCategories(response.data)
            )
            .catch((error) => (
                setError(error)
            ))
    }, [])

    if (error) return `Error: ${error.message}`

    return (
        user &&
        <div className="relative min-h-screen bg-gray-100">
            <NavBar
                buttonContent="guide"
                disableButton={user.accountType === "STUDENT"}
                component={<NewButton content='guide' path='/create' />}
                user={user}
            />
            <div className="py-10">
                <div className="max-w-3xl mx-auto sm:px-6 lg:max-w-7xl lg:px-8 lg:grid lg:grid-cols-12 lg:gap-8">
                    <div className="hidden lg:block lg:col-span-3 xl:col-span-2">
                        <SideBar user={user} />
                    </div>
                    <main className="lg:col-span-9 xl:col-span-10">
                        <GuideCategories categories={categories} />

                        <div className="mt-4">
                            <h1 className="sr-only">Guides</h1>
                            <GuideList
                                items={guides}
                                contentType="guides"
                                path="/guideDetails"
                                setGuides={setGuides}
                                user={user}
                            />
                        </div>
                    </main>
                </div>
            </div>
        </div>

    )
}
